

<div class="collapse multi-collapse" id="addProject">
  <div class="card card-body">
    <form class="addProject-form" method="post" autocomplete="off">
      <div class="addProject-container input-group">
        <input name="project" type="text" class="addProject-input form-control" placeholder="New project"
               aria-label="New task" aria-describedby="basic-addon2"
               data-toggle="popover" data-placement="top" data-content="This project already exist.">
        <div class="addProject-btn-box input-group-append">
          <button class="addProject-btn  btn btn-success" type="submit">
            <i class="addProject-icon material-icons">add</i>
          </button>
        </div>
      </div>
    </form>
  </div>
</div>